mapper.select:
		select id_ from town


					and county_id=#{cm.countyId}


					and remark_=#{cm.remark}


				and (
                	name_ like CONCAT('%','${cm.keyword}','%')
                	or spell_ like CONCAT('%','${cm.keyword}','%')
					or county_id like CONCAT('%','${cm.keyword}','%')
					or remark_ like CONCAT('%','${cm.keyword}','%')
				)



			order by ${cm.orderBy}


			${cm.sortAsc}
